-- 데이터베이스 생성
CREATE DATABASE IF NOT EXISTS diet_recommendation_db;
USE diet_recommendation_db;

-- 사용자 계정 테이블
CREATE TABLE users (
    id INT PRIMARY KEY AUTO_INCREMENT,
    email VARCHAR(255) UNIQUE NOT NULL,
    password VARCHAR(255) NOT NULL,
    username VARCHAR(50) NOT NULL,
    birthdate VARCHAR(8) NOT NULL,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
);

-- 사용자 신체 정보 테이블
CREATE TABLE user_physical_info (
    id INT PRIMARY KEY AUTO_INCREMENT,
    user_id INT NOT NULL,                              -- 사용자 ID
    current_weight DECIMAL(5,2) NOT NULL,             -- 현재 체중 (kg)
    target_weight DECIMAL(5,2) NOT NULL,              -- 목표 체중 (kg)
    height DECIMAL(5,2) NOT NULL,                     -- 키 (cm)
    age INT NOT NULL,                                  -- 나이 (세)
    gender ENUM('Male', 'Female') NOT NULL,           -- 성별
    activity_level TINYINT NOT NULL,                  -- 활동 수준 (1~4)
    goal_type ENUM('저지방 고단백', '균형 식단', '벌크업') NOT NULL, -- 목표 식단 유형
    basal_metabolic_rate DECIMAL(8,2),                -- 현재 BMR (자동 계산)
    active_metabolic_rate DECIMAL(8,2),               -- 현재 AMR (자동 계산)
    target_basal_metabolic_rate DECIMAL(8,2),         -- 목표 BMR (목표 체중 기준)
    target_active_metabolic_rate DECIMAL(8,2),        -- 목표 AMR (목표 체중 기준)
    bmi DECIMAL(4,1),                                 -- 현재 BMI (자동 계산)
    body_fat_percentage DECIMAL(4,1),                 -- 현재 체지방률 (자동 계산)
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,   -- 생성 시간
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, -- 업데이트 시간
    FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE
);

-- 음식 데이터베이스 테이블
CREATE TABLE foods (
    id INT PRIMARY KEY AUTO_INCREMENT,
    name VARCHAR(100) NOT NULL,
    category VARCHAR(50) NOT NULL,  -- 밥류, 빵 및 과자류...
    calories INT NOT NULL,
    carbs DECIMAL(6,2) NOT NULL,    -- g 단위
    protein DECIMAL(6,2) NOT NULL,  -- g 단위
    fat DECIMAL(6,2) NOT NULL,      -- g 단위
    sugar DECIMAL(6,2) NOT NULL,    -- g 단위 (당 성분)
    serving_size VARCHAR(50) NOT NULL,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE `exercise_base` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `base_calories_burned` double NOT NULL,
  `body_part` enum('가슴','등','삼두','스포츠','어깨','유산소','이두','전완근','코어','하체') NOT NULL,
  `created_at` datetime(6) DEFAULT NULL,
  `description` text,
  `exercise_category` enum('상체근력운동','유산소운동','유연성및스트레칭운동','전신강화운동','코어강화운동','하체근력운동') NOT NULL,
  `media_url` varchar(255) DEFAULT NULL,
  `name` varchar(255) NOT NULL,
  `updated_at` datetime(6) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `idx_name` (`name`),
  KEY `idx_exercise_category` (`exercise_category`),
  KEY `idx_body_part` (`body_part`)
) ENGINE=InnoDB AUTO_INCREMENT=2048 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

CREATE TABLE `exercise_difficulty_mapping` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `activity_level` int DEFAULT NULL,
  `age_range_end` int DEFAULT NULL,
  `age_range_start` int DEFAULT NULL,
  `bmi_range_end` double DEFAULT NULL,
  `bmi_range_start` double DEFAULT NULL,
  `created_at` datetime(6) DEFAULT NULL,
  `recommended_intensity` enum('낮음','높음','중간') DEFAULT NULL,
  `updated_at` datetime(6) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=81 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

CREATE TABLE `exercise_preferences` (
  `id` int NOT NULL AUTO_INCREMENT,
  `user_id` int NOT NULL,
  `preferred_body_part` enum('가슴','등','삼두','스포츠','어깨','유산소','이두','전완근','코어','하체') DEFAULT NULL,
  `preferred_category` enum('상체근력운동','유산소운동','유연성및스트레칭운동','전신강화운동','코어강화운동','하체근력운동') DEFAULT NULL,
  `preferred_intensity` enum('낮음','높음','중간') DEFAULT NULL,
  `preferred_time_of_day` time(6) DEFAULT NULL,
  `created_at` TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  `updated_at` TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE
);

